        double sum = 0.0;
        rep(l, M+1) {
          if(T[i][j] < T[k][l]) sum += P[k][l];
        }
        losers_prob *= sum;
      }
      res += losers_prob * P[i][j];
    }
    printf("%.10f\n", res);
  }
}

