	rep(i,n){
		rep(j,m + 1){
			double rest_p = 1;
			double run_p = 1;
			for(int k = 0; k < j; k++){
				rest_p *= p[i];
			}
			for(int k = j; k < m; k++){
				run_p *= (1 - p[i]);
			}
			c[i][j] = rest_p * run_p * pas[m][j];
		}
	}


