double Pow(double a, int b) {
	double ret = 1.0;
	while (b) {
		if (b & 1)
			ret = ret * a;
		b >>= 1;
		a = a * a;
	}
	return ret;
}

inline void Add(int i, int x, double v) {
	if (x == 0)
		return;
	for (; x <= tot; x += lowbit(x))
		co[i][x] += v;
}

inline double Query(int i, int x) {
	double ret = 0;
	for (; x > 0; x -= lowbit(x))
		ret += co[i][x];
	return ret;
}
