DB f(int i, DB ti){
    DB z = 1; REP(j, n) if (i != j){
        if (t0[j] + m*t[j] <= ti) return 0;
        REP(k, m+1) if (t0[j] + k*t[j] > ti){
            z *= (1 - sp[j][k]);
            break;
        }
    }
    return z;
}

