double dp[109][109];

void init(){
	memset(dp,0,sizeof(dp));
	for(int i=0;i<=101;i++){
		dp[0][i] = dp[i][0] = dp[i][i] =1.0;
		dp[1][i]=i+0.0;
	}
	dp[0][0]=0;
	for(int i=1;i<=101;i++)
		for(int j=1;j<=101;j++){
			if( i != j)
				dp[i][j] = (dp[i][j-1] + dp[i-1][j-1]);
		}

}
double cc(int num,int m){
	return dp[num][m];
}
double _pow(double p,int k){
	double tmp=1.0;
	for(int i=0;i<k;i++)
		tmp*=p;
	return tmp;
}
double cul(int num,double p){
	return cc(num,m)*_pow(p*1.0,num)*_pow(1.0*(1-p),m-num);
}
int main()
{
	init();
	while(scanf("%d%d%d", &n, &m, &l)!=EOF){

