				double pp = cul(xx, p[wp]);

				for(int lp=0; lp<n;lp++){

					if(lp == wp)
						continue;
					double np=0;
					for(int xi=0; xi<=m; xi++){

						double lt = l/v[lp] + xi*t[lp];
						if(lt > at){
							np += cul(xi, p[lp]);
						}
					}

					pp *= np;
				}

				wpp += pp;
			}
			printf("%.8lf\n",wpp);
		}
	}


	return 0;
}

