#include <cstdio>
#include <cmath>
#include <algorithm>
using namespace std;

const int MAX_N = 100;
const int MAX_M = 50;
long long int comb[MAX_M+5][MAX_M+5];
int P[MAX_N+5], T[MAX_N+5], V[MAX_N+5];
double prob[MAX_N+5][MAX_M+5];
double goal_t[MAX_N+5][MAX_M+5];

void init_comb() {
    comb[0][0] = 1;
    for(int i=1; i<=MAX_M; i++) {
        comb[i][0] = 1;
        for(int j=1; j<=MAX_M; j++) {
            comb[i][j] = comb[i-1][j-1] + comb[i-1][j];
        }
    }
}

int main() {
    init_comb();
    int N, M, L; scanf("%d%d%d", &N, &M, &L);
    for(int i=0; i<N; i++) {
        scanf("%d%d%d", &P[i], &T[i], &V[i]);
        for(int k=0; k<=M; k++) {
            goal_t[i][k] = 1.0 * L / V[i] + k * T[i];
            prob[i][k] = comb[M][k] * pow(P[i] / 100.0, k) * pow((100-P[i]) / 100.0, M-k);
            if(k != 0) prob[i][k] += prob[i][k-1];
        }
    }

    for(int i=0; i<N; i++) {
        double ans = 0.0;
        for(int k=0; k<=M; k++) {
            double player_t = goal_t[i][k], tmp = prob[i][k] - (k != 0 ? prob[i][k-1] : 0);
            for(int j=0; j<N; j++) {
                if(i == j) continue;
                // 
                if(player_t > goal_t[j][M]) tmp = 0;
                // 
                else if(player_t < goal_t[j][0]) continue;
                else {
                    int idx = upper_bound(goal_t[j], goal_t[j]+M+1, player_t) - goal_t[j] - 1;
                    tmp *= (1.0 - prob[j][idx]);
                }
            }
            // printf("initial tmp = %.12f, current = %.12f\n", prob[i][k], tmp);
            ans += tmp;
        }
        printf("%.12f\n", ans);
    }
    return 0;
}

