	}

	void inspect() {
		cerr << "goal_times" << endl;
		REP(i, goal_times.size()) {
			cerr << " " << goal_times[i];
		}
		cerr << endl;
		cerr << "probabilities" << endl;
		REP(i, probabilities.size()) {
			cerr << " " << probabilities[i];
		}
		cerr << endl;
	}
};

ll extgcd(ll a, ll b, ll &x, ll &y)
{
	ll g = a; x = 1; y = 0;
	if (b != 0) g = extgcd(b, a % b, y, x), y -= (a / b) * x;
	return g;
}

int mod_inverse(int a, int m)
{
	ll x, y;
	extgcd(a, m, x, y);
	return (m + x % m) % m;
}

