#include<stdio.h>
#include<algorithm>
double C(int a,int b)
{
	b=std::min(a-b,b);
	double r=1;
	for(int i=1;i<=b;++i)r=r*(a-b+i)/i;
	return r;
}
double F(double a,int b)
{
	double r=1,x=a;
	for(;b;b>>=1)
	{
		if(b&1)r*=x;
		x*=x;
	}
	return r;
}
int n,m,l;
int p[100],t[100],v[100];
double EPS = 5e-10;
typedef std::pair<double,double>P;
P*q,a[100][51];
double R(int i,int j){return C(m,j)*F(p[i]/100.0,j)*F(1-p[i]/100.0,m-j);}
int main()
{
	scanf("%d%d%d",&n,&m,&l);;
	int i,j,k;
	for(i=0;i<n;++i)
	{
		scanf("%d%d%d",&p[i],&t[i],&v[i]);
		double x=0,y;
		for(j=0;j<=m;++j)
		{
			a[i][j].first = j*t[i] + (double)l/v[i];
			y=R(i,j);
			a[i][j].second=y+x;
			x+=y;
		}
	}
	double r,x;
	for(i=0;i<n;++i)
	{
		r=0;
		for(j=0;j<=m;++j)
		{
			x=1;
			for(k=0;k<n;++k)
			{
				if(i==k)continue;
				if(a[i][j].first+EPS < a[k][0].first);
				else if(a[i][j].first-EPS > a[k][m].first)x=0;
				else
				{
					q=std::lower_bound(a[k],a[k]+m+1,P(a[i][j].first+EPS,-1));
					x*=(1-q[-1].second);
				}
			}
			r+=x*R(i,j);
		}
		printf("%.12f\n",r);
	}
	return 0;
}

