int C(int n,int m)
{
    int a=1,b=1;
    for(int i=m;i>=m-n+1;i--)
        a*=i;
    for(int i=1;i<=n;i++)
        b*=i;
    return a/b;
}

double get(int c,int n,double p)
{
    return C(c,n)*pow(p,c)*pow((1-p),n-c);
}

int main()
{
