using namespace std;

const int maxn = 105;
const int maxm = 55;

double c[maxm][maxm], ans[maxn], p[maxn], t[maxn], v[maxn];
double q[maxn][maxm*maxn];
int n, m;
double l;
struct A {
	int id;
	double p, t;
} a[maxn * maxm];

double calc(double k, int x, int y) {
	for(int i = 1; i <= y; ++ i) k *= p[x]/100.0;
	for(int i = 1; i <= m-y; ++ i) k *= (100.0-p[x])/100.0;
	return k;
}

bool cmp(A a, A b) {
	return a.t < b.t;
}

int main () {
	int tot = 0;
	while(scanf("%d%d%lf", &n, &m, &l)!=-1){
	tot = 0;
	memset(v, 0, sizeof v);
	for(int i = 0; i <= m; ++ i) {
		c[i][0] = 1;
		for(int j = 1; j <= i; ++ j) {
			c[i][j] = c[i-1][j] + c[i-1][j-1];
		}
	}
	for(int i = 1; i <= n; ++ i) {
		scanf("%lf%lf%lf", &p[i], &t[i], &v[i]);
	}
	for(int i = 1; i <= n; ++ i) {
		for(int j = 0; j <= m; ++ j) {
			++ tot;
			a[tot].id = i;
			a[tot].p = calc(c[m][j], i, j);
			a[tot].t = l/v[i] + j * t[i];
		}
	}
	sort(a+1, a+tot+1, cmp);
	memset(ans, 0, sizeof ans);
	memset(q, 0, sizeof q);
	for(int i = 1; i <= tot; ++ i) {
		double P = 1;
		int k = i-1;
		while(a[k].t == a[i].t) k--;
		for(int j = 1; j <= n; ++ j) {
			if(j == a[i].id) continue;
			P *= q[j][k];
		}
		ans[a[i].id] += P*a[i].p;
		for(int j = 1; j <= n; ++ j) {
			if(j == a[i].id) q[a[i].id][i] = q[a[i].id][i-1] + a[i].p;
			else q[j][i] = q[j][i-1];
		}
	}
	for(int i = 1; i <= n; ++ i) printf("%.8lf\n", ans[i]);
	}
}

