#include<bits/stdc++.h>
using namespace std;

int N,M,L;
int P[111],T[111],V[111];

double Time[111][111];
double pa[111][111];
double ncr[111][111];

const double EPS = 1E-9;
const double INF = 1E50;

void getncr(){
  for(int i=0;i<=100;i++) ncr[i][i] = ncr[i][0] = 1LL;
  for(int i=2;i<=100;i++)
    for(int j=1;j<i;j++)
      ncr[i][j] = ncr[i-1][j] + ncr[i-1][j-1];

}

int main(){
  cin >> N >> M >> L;
  for(int i=0;i<N;i++){
    cin >> P[i] >> T[i] >> V[i];
  }

  getncr();

  for(int i=0;i<N;i++){
    double ka = 1.0f;
    for(int l=0;l<M;l++){
      ka *= (double)(100-P[i])/100.0;
    }	
    //  cout <<i << endl;
    for(int j=0;j<=M;j++){
      if( V[i] == 0 )
	Time[i][j] = INF;
      else
	Time[i][j] = (double)L / (double)V[i] + (double)T[i]*(double)j;

      pa[i][j] = ka*ncr[M][j];

      if( P[i] == 100 )
	ka = 1.0f;
      else 
	ka /= (double)(100-P[i])/100.0;
      ka *= (double)P[i]/100.0;       
    }
  }

  for(int i=0;i<N;i++){
    double res = 0.0f;
    for(int j=0;j<=M;j++){

      double H = Time[i][j];
      double ret = 1.0f;
      for(int k=0;k<N;k++){
	if( i == k ) continue;
	double kk = 0.0f;

	for(int l=0;l<=M;l++){
	  //  cout << H << " " << Time[k][l] << " " << pa[k][l] << endl;
	  if( Time[k][l] > H+EPS ){
	    //  cout << pa[k][l] << endl;
	    kk += pa[k][l];
	  }
	}

	//	cout << "k= " << k << " "<< kk << endl;
	ret *= kk;
      }
      //      cout << "i , j = " << i << " "<< j << " "<< ret << " * " << pa[i][j] << " "<< ret*pa[i][j] << endl;
      res += ret*pa[i][j];
    }

    printf("%.10lf\n",res);
  }

}

