using namespace std;

const double EPS=1e-7;
const double INF=1e77;

const int N_BIN=50;
double nCr[N_BIN+1][N_BIN+1];
void binom(){
	rep(n,N_BIN+1) nCr[n][0]=1;
	rep(n,N_BIN) rep(r,n+1) nCr[n+1][r+1]=nCr[n][r+1]+nCr[n][r];
}

int main(){
	binom();

	int n,m,L; scanf("%d%d%d",&n,&m,&L);
	double p[100];
	int rest[100],v[100];
	rep(i,n) scanf("%lf%d%d",p+i,rest+i,v+i), p[i]/=100;

	double t[100][51],q[100][51];
	rep(i,n) rep(j,m+1) {
		if(v[i]>0) t[i][j]=(double)L/v[i]+j*rest[i];
		else       t[i][j]=INF;
		q[i][j]=nCr[m][j]*pow(p[i],j)*pow(1-p[i],m-j);
	}

	rep(i,n){
		if(v[i]==0){ puts("0"); continue; }

		double ans=0;
		rep(j,m+1){
			double pr=q[i][j];
			rep(k,n) if(i!=k) {
				double tmp=0;
				rep(l,m+1) if(t[k][l]<0 || t[i][j]+EPS<t[k][l]) tmp+=q[k][l];
				pr*=tmp;
			}
			ans+=pr;
		}
		printf("%.15f\n",ans);
	}

	return 0;
}

