double tmp;
double solve(double p,int m,int n)
{
    double ret=1;
    for(int i=0;i<n;i++)
    {
        ret=ret*(m-i);
        ret=ret/(i+1);
        ret=ret*p;
    }
    for(int i=n;i<m;i++)
    {
        ret=ret*(1.0-p);
    }

    return ret;
}
