	dp[0][0]=1;
	for (int i=1; i<=50; ++i) 
	{
		dp[i][0]=dp[i][i]=1;
		for (int j=1; j<i; ++j)
			dp[i][j]=(dp[i-1][j]+dp[i-1][j-1]);
	}
}
double cc(int num,int m){
	return double(dp[m][num]);
}
double _pow(double p,int k){
	double tmp=1.0;
	for(int i=0;i<k;i++)
		tmp*=p;
	return tmp;
}
double cul(int num,double p){
	return cc(num,m)*_pow(p*1.0,num)*_pow(1.0*(1-p),m-num);
}
