double eps=1e-9,inf=1e10;
typedef pair<double,double> P;
typedef pair<P,int> PP;
vector<PP> vc;
int main(){
	int n,m,l;
	cin>>n>>m>>l;
	rep(i,n){
		double p,t,v;
		cin>>p>>t>>v;
		p/=100;
		if(p==1){
			double tt;
			if(v==0) tt=inf;
			else tt=l/v+m*t;
			vc.pb(PP(P(tt,1),i));
			//cout << "i" << i << " " << tt << " " << 1 << endl;
			continue;
		}
		if(p==0){
			double tt;
			if(v==0) tt=inf;
