			vc.pb(PP(P(tt,1),i));
			//cout << "i" << i << " " << tt << " " << 1 << endl;
			continue;
		}
		double pro=1;
		rep(j,m) pro*=(1-p);
		rep(j,m+1){
			double tt;
			if(v==0) tt=inf;
			else tt=l/v+j*t;
			vc.pb(PP(P(tt,pro),i));
			//cout << "i" << i << " " << tt << " " << pro << endl;
			pro*=p/(1-p);
			pro*=(double)(m-j)/(j+1);
		}
	}
