	rep(i,vc.size()){
		if(vc[i].fs.fs>1e9) break;
		double cnt[100]={};
		for(int j=i+1;j<vc.size();j++){
			if(vc[j].fs.fs<vc[i].fs.fs+eps) continue;
			cnt[vc[j].sc]+=vc[j].fs.sc;
		}
		double ret=1;
		rep(j,n){
			if(j==vc[i].sc) ret*=vc[i].fs.sc;
			else ret*=cnt[j];
		}
		ans[vc[i].sc]+=ret;
	}
	rep(i,n) printf("%.9f\n",ans[i]);
}

