			int M,L;
			cin >> M >> L;
			for(int j = 0 ; j < M ; j++){
				int s,e;
				cin >> s >> e;
				s -= 6;
				e -= 6;
				for(int k = 0 ; k < (1<<16) ; k++){
					dp[i+1][k] = max(dp[i][k],dp[i+1][k]);
					int b = ((1<<(e-s))-1) << s;
					if( k & b ) continue;
					dp[i+1][k|b] = max(dp[i+1][k|b],dp[i][k]+L);
				}
			}
		}
		int res = 0;
		for(int i = 0 ; i < (1<<16) ; i++)
			res = max(res,dp[N][i]);
		cout << res << endl;
	}
}

