#include<iostream>
#include<algorithm>
using namespace std;

#define MAX_Q 100000
#define MAX_N 120
#define MAX_M 18
#define MAX_T 17

long long dp[1 << MAX_M];
long long dp2[1 << MAX_M];

long long P[MAX_N][MAX_N][2];
long long N, M[MAX_M], Q[MAX_N];
int Q1[MAX_Q];

long long power(int le, int ri) {
	long long cnt2 = 0;
	for (int i = le; i < ri; i++) {
		cnt2 += (1 << i);
	}
	return cnt2;
}

long long bit_dp() {
	long long cnt = 0;
	for (int i = 1; i <= N; i++)
	{
		for (int j = 0; j < (1 << MAX_T); j++)
		{
			long long V[MAX_M];
			for (int k = 0; k < MAX_T; k++) {
				V[k] = (j / (1 << k)) % 2;
			}

			for (int k = 0; k < M[i]; k++)
			{
				long long L = P[i][k][0], R = P[i][k][1];
				for (int l = L; l < R; l++)
				{
					if (V[l] == 1)
					{
						goto E;
					}
				}
				cnt = j + power(L, R);
				dp2[cnt] = max(dp2[cnt], dp[j] + Q[i]);
			E:;
			}
		}
		for (int j = 0; j < (1 << MAX_T); j++)
		{
			dp[j] = max(dp[j], dp2[j]);
			dp2[j] = 0;
		}
	}
	long long maxn = 0;
	for (int i = 0; i < (1 << MAX_T); i++)
	{
		maxn = max(maxn, dp[i]);
	}
	return maxn;
}

void _memset() {
	for (int i = 0; i < (1 << MAX_M); i++) {
		dp[i] = 0;
		dp2[i] = 0;
	}
	for (int i = 0; i < MAX_N; i++) {
		Q[i] = 0;
		for (int j = 0; j < MAX_N; j++) {
			P[i][j][0] = 0;
			P[i][j][1] = 0;
		}
	}
}

int main() {
	int cnt3 = 0;
	while (true) {
		_memset();
		cin >> N;
		if (N == 0) {
			break;
		}
		for (int i = 1; i <= N; i++) {
			cin >> M[i] >> Q[i];
			for (int j = 0; j < M[i]; j++) {
				cin >> P[i][j][0] >> P[i][j][1];
				P[i][j][0] -= 6;
				P[i][j][1] -= 6;
			}
		}
		Q1[cnt3] = bit_dp();
		cnt3++;
	}
	for (int i = 0; i < cnt3; i++) {
		cout << Q1[i] << endl;
	}
	return 0;
}

#include<iostream>
using namespace std;

long long int dp[110][1<<16];

int main(int argc, char *argv[]){
  int n;
  while(cin >> n, n){
    int m, l[n], t[n];
    memset(t, 0, sizeof(t));
    for (int i = 0; i < n; i++) {
      cin >> m >> l[i];
      int s, e;
      for (int j = 0; j < m; j++) {
        cin >> s >> e;
        for (int k = s - 6; k < e - 6; k++)
          t[i] |= 1<<k;
      }
    }
    for (int i = 0; i <= n; i++) {
      for (int j = 0; j < (1<<16); j++) {
        dp[i][j] = -1;
      }      
    }
    dp[0][0] = 0;
    for (int i = 0; i < n; i++) {
      for (int j = 0; j < (1<<16); j++) {
        if(dp[i][j] < 0)continue;
        dp[i + 1][j] = max(dp[i + 1][j], dp[i][j]);
        if((t[i]&j) > 0)continue;
        dp[i + 1][j | t[i]] = max(dp[i + 1][j | t[i]], dp[i][j] + l[i]);
      }
    }
    long long int ans = 0;
    for (int i = 0; i < (1<<16); i++) {
      ans = max(ans, dp[n][i]);
    }
    std::cout << ans << std::endl;
  }
  return 0;
}

