#define rep(i,n) rep2(i,0,n)
#define rep2(i,m,n) for(int i=m;i<n;i++)

int N,date[111],point[111]; // 男の数、各男のデートの時間帯、各男の満足度
int dp[111][1<<16]; // Dynamic Programming で使うメモ。
					// 関数 f(cur,mask) の返り値を dp[cur][mask]に保存する。
					// 男は100人までだが、cur=100もありうるので1つ目の[]の中は101以上必要

// 【デート日程の整数での表し方】
// 例1: 000000000000111(2進数) は「6時〜9時」の意味
// 例2: 000011110011000(2進数) は「10時〜12時と14時〜18時」の意味
// 最下位の桁が6時〜7時、最上位の桁が21時〜22時に対応

int f(int cur,int mask){
	// cur番以降の男とだけデートするとき、maskの時間帯で得られる最大の満足度
	// (番号は0番〜N-1番。ただし、cur=Nもありうる)
	int& res=dp[cur][mask]; // 答えをメモする場所
	if(res>-1)return res; // メモに書かれている数が-1でなかった
						  // = すでに計算されていた。メモされた返り値を返して終了

	// ここまで来た場合、関数 f(cur,mask) が呼ばれたのは初めて。
	// 最大の満足度を計算し、その結果を dp[cur][mask] (res) に書き込む
	if(cur==N)return res=0; // N番以降には誰もいないので、誰ともデートできない
							// メモに0と書き、0を返して終了

	// ここまで来た場合、cur番の男が実在する。
	// 選択肢1.「cur番の男とデートしない」
	// 選択肢2.「cur番の男とデートする」(maskがcur番の男のデート日程を含んでいる場合のみ)
	res=f(cur+1,mask);
	// 選択肢1。この場合、「cur+1番以降の男とデートして得られる最大の満足度」を得る。
	// 結果はメモに書き込んでおく
	if((date[cur]&mask)==date[cur]){ // mask は date[cur] を含むか？
		res=max(res,point[cur]+f(cur+1,mask^date[cur]));
		// 選択肢2。この場合、「cur番の男の満足度」+
		// 「残った時間帯でcur+1番以降の男とデートして得られる最大の満足度」を得る。

		// max(a,b) でaとbのうち大きい方が返る。
		// 選択肢2の結果が現在のメモの値(選択肢1の結果)を上回ったら、メモを上書きする
	}
	return res; // ここでメモに書き込まれているのが最大の満足度。それを返す
}

int main(){
	while(cin>>N && N){ // Nに0が入力されるまで続ける
		rep(i,N){
			int M; // デート回数
			cin>>M>>point[i];
			date[i]=0; // デートの時間帯を初期化
			rep(j,M){
				int S,E; // 開始時刻、終了時刻
				cin>>S>>E;
				rep2(k,S,E)date[i]|=1<<(k-6);
				// 例: S=10, E=13なら, 
				// 下から(10-6=)4番目、5番目、6番目のビットを1にする
			}
		}
		memset(dp,-1,sizeof(dp)); // 配列dpの要素をすべて-1に
		cout<<f(0,(1<<16)-1)<<endl;
		// 答えは「0番以降の男とデートするとき、6時〜22時の間に得られる最大の満足度」。
		// 1111111111111111(2進数,16桁) = 2^16 - 1
	}
}

#include <bits/stdc++.h>

using namespace std;

typedef long long ll;
typedef vector<int> vi;

const int MAX_N = 110;
const int BIT_SZ = (1<<(22-6));

ll dp[MAX_N][BIT_SZ+10];



int main() {
	int N;
	while(cin >> N) {
		if(N == 0) break;
		vector<int> M(N), L(N);
		vector<vi> S(N), E(N);
		vector<int> time(N);
		//input
		for(int i = 0; i < N; i++) {
			cin >> M[i] >> L[i];
			S[i].resize(M[i]);
			E[i].resize(M[i]);
			for(int j = 0; j < M[i]; j++) cin >> S[i][j] >> E[i][j];
			for(int j = 0; j < M[i]; j++) {
				for(int b = S[i][j]; b < E[i][j]; b++) time[i] |= (1<<(b-6));
			}
		}
		// init
		for(int i = 0; i < MAX_N; i++) for(int j = 0; j < BIT_SZ+10; j++) {
			dp[i][j] = 0LL;
		}

		for(int i = 0; i < N; i++) {
			for(int mask = 0; mask < BIT_SZ; mask++) {
				if(mask & time[i]) continue;
				int nxtMask = mask | time[i];
				dp[i+1][nxtMask] = max({dp[i+1][nxtMask], dp[i][nxtMask], dp[i][mask]+L[i]});
			}
		}
		ll ans = 0LL;
		for(int mask = 0; mask < BIT_SZ; mask++) ans = max(ans, dp[N][mask]);
		cout << ans << endl;
	}
	return 0;
}

