const double EPS = 1e-10;
const double PI  = acos(-1.0);

LL dp[2][1<<17];
int main(){
  cin.tie(0);
  ios_base::sync_with_stdio(false);

  int N;
  while(cin >> N,N){
	VI sat(N);
	VI sched(N, 0);
	REP(i,N){
	  int M; cin >> M >> sat[i];
	  REP(j,M){
		int s, e;
		cin >> s >> e;
		s -= 6, e -= 6;
		FOR(k,s,e)
		  sched[i] |= 1 << k;
	  }
	}

	LL ans = 0;
	int crt = 0, nxt = 1;
	fill((LL*)dp, (LL*)dp+2*(1<<17), -1);
	dp[crt][0] = 0;
	REP(i,N){
	  for(int bit=0;bit<(1<<17);++bit)
		dp[nxt][bit] = dp[crt][bit];
	  for(int bit=0;bit<(1<<17);++bit){
		if(dp[crt][bit] < 0) continue;
		if(bit & sched[i]) continue;
		dp[nxt][bit | sched[i]] = max(dp[nxt][bit | sched[i]],
									  dp[crt][bit] + sat[i]);
	  }
	  swap(crt, nxt);
	}
	REP(bit,(1<<17))
	  ans = max(ans, dp[crt][bit]);
	cout << ans << endl;
  }

  return 0;
}

