				{
					unsigned long las = nes ^ dat[i].sch.to_ulong();
					ndp[nes] = max(ndp[nes], dp[las] + dat[i].cost);
				}
			}
			for (int j = 0; j < (1 << 16); ++j)
			{
				dp[j] = ndp[j];
			}
		}
		cout << dp[(1 << 16) - 1] << endl;
	}
	return 0;
}

