using namespace std;

#define MAX_N 120
#define MAX_M 18
#define MAX_T 16
int dp[1 << MAX_M];
int dp2[1 << MAX_M];
int P[MAX_N][MAX_N][2];
int N, M[MAX_M], Q[MAX_N];

int power(int le, int ri) {
	int cnt2 = 0;
	for (int i = le; i < ri; i++) {
		cnt2 += (1 << i);
	}
	return cnt2;
}

int bit_dp() {
	int cnt = 0;
	for (int i = 1; i <= N; i++) {
		for (int j = 0; j < (1 << MAX_T); j++) {
			int V[MAX_M];
			for (int k = 0; k < MAX_T; k++) {
				V[k] = (j / (1 << k)) % 2;
			}
			for (int k = 0; k < M[i]; k++) {
				int L = P[i][k][0], R = P[i][k][1];
				for (int l = L; l < R; l++) {
					if (V[l] == 1) {
						goto E;
					}
				}
				cnt = j + power(L, R);
				dp2[cnt] = max(dp2[cnt], dp[j] + Q[i]);
			E:;
			}
		}
		for (int j = 0; j < (1 << MAX_T); j++) {
			dp[j] = max(dp[j], dp2[j]);
			dp2[j] = 0;
		}
	}
	int maxn = 0;
	for (int i = 0; i < (1 << MAX_T); i++) {
		maxn = max(maxn, dp[i]);
	}
	return maxn;
}

void _memset() {
	for (int i = 0; i < (1 << MAX_M); i++) {
		dp[i] = 0;
		dp2[i] = 0;
	}
	for (int i = 0; i < MAX_N; i++) {
		Q[i] = 0;
		for (int j = 0; j < MAX_N; j++) {
			P[i][j][0] = 0;
			P[i][j][1] = 0;
		}
	}
}

int main() {
	while (true) {
		_memset();
		cin >> N;
		if (N == 0) {
			break;
		}
		for (int i = 1; i <= N; i++) {
			cin >> M[i] >> Q[i];
			for (int j = 0; j < M[i]; j++) {
				cin >> P[i][j][0] >> P[i][j][1];
				P[i][j][0] -= 6;
				P[i][j][1] -= 6;
			}
		}
		cout << bit_dp() << endl;
	}
	return 0;
}

