    for(int bit = 0; bit < (1 << 16); bit++){
      if(bit & mask[0]) continue;
      dp[0][bit | mask[0]] = L[0];
    }
    for(int i = 1; i < N; i++){
      for(int bit = 0; bit < (1 << 16); bit++){
	if(bit & mask[i]) continue;
	dp[i][bit | mask[i]] = max(dp[i][bit | mask[i]], dp[i-1][bit] + L[i]);
      }
    }
    cout << *max_element(dp[N-1], dp[N-1] + (1 << 16)) << endl;
  }
  return 0;
}

