void solve() {
    int n; scanf("%d", &n); if (n == 0) exit(0);
    vector<ll> dp(1 << 16);
    for (int i = 0; i < n; i++) {
        int S = 0;
        int m; ll D; scanf("%d %lld", &m, &D);
        for (int i = 0; i < m; i++) {
            int f, t; scanf("%d %d", &f, &t);
            f -= 6; t -= 6;
            for (int j = f; j < t; j++) {
                S |= (1 << j);
            }
        }
        chmax(dp[S], D);
    }
    for (int S = 0; S < (1 << 16); S++) {
        for (int T = S & (S - 1); T > 0; T = (T - 1) & S) {
            chmax(dp[S], dp[T] + dp[S ^ T]);
        }
    }
    printf("%lld\n", dp[(1 << 16) - 1]);
}

