int main() {
	int N;
	while(cin >> N) {
		if(N == 0) break;
		vector<int> M(N), L(N);
		vector<vi> S(N), E(N);
		vector<int> time(N);
		//input
		for(int i = 0; i < N; i++) {
			cin >> M[i] >> L[i];
			S[i].resize(M[i]);
			E[i].resize(M[i]);
			for(int j = 0; j < M[i]; j++) cin >> S[i][j] >> E[i][j];
			for(int j = 0; j < M[i]; j++) {
				for(int b = S[i][j]; b < E[i][j]; b++) time[i] |= (1<<(b-6));
			}
		}
		// init
		for(int i = 0; i < MAX_N; i++) for(int j = 0; j < BIT_SZ+10; j++) {
			dp[i][j] = 0LL;
		}

		for(int i = 0; i < N; i++) {
			for(int mask = 0; mask < BIT_SZ; mask++) dp[i+1][mask] = dp[i][mask];
			for(int mask = 0; mask < BIT_SZ; mask++) {
				if(mask & time[i]) continue;
				int nxtMask = mask | time[i];
				dp[i+1][nxtMask] = max({dp[i+1][nxtMask], dp[i][mask]+L[i]});
			}
		}
		ll ans = 0LL;
		for(int mask = 0; mask < BIT_SZ; mask++) ans = max(ans, dp[N][mask]);
