ll solve(int n,int S){
  if(n == N){ return 0LL; }
  if(dp[n][S] != -1){
    return dp[n][S];
  }
  ll res = 0LL;
  res = max(res,solve(n+1,S));
  bool ok = true;
  for(int i = 0 ; i < 16 ; i++){
    if((S >> i) & (bit[n] >> i)){
      ok = false;
      break;
    }
  }
  if(ok){
    res = max(res,solve(n+1,S|bit[n])+L[n]);
  }
  return dp[n][S] = res;
}

