#include <bits/stdc++.h>
using namespace std;

int solve(int N, vector<int>& T, vector<int>& L) {
  constexpr int R = 1 << 16;
  int res = L[0];
  vector<vector<int>> dp(N, vector<int>(R, 0));
  for(int S = 0; S < R; S++) {
    if((S & T[0]) == T[0]) {
      dp[0][T[0]] = L[0];
    }
  }
  for(int i = 1; i < N; i++) {
    for(int S = 0; S < R; S++) {
      dp[i][S] = dp[i - 1][S];
      if((S & T[i]) == T[i]) {
	dp[i][S] = max(dp[i][S], L[i] + dp[i - 1][S & (~T[i])]);
	res = max(res, dp[i][S]);
      }
    } 
  }
  return res;
}

int main() {
  int N;
  while(cin >> N, N != 0) {
    vector<int> L(N);
    vector<int> T(N);
    for (int i = 0; i < N; i++) {
      int M;
      cin >> M >> L[i];
      int t = 0;
      for(int j = 0; j < M; j++) {
	int s, e;
	cin >> s >> e;
	for (int k = s; k < e; k++) t |= 1 << k-6;
      } 
      T[i] = t;
    }
    cout << solve(N, T, L) << endl;
  }
}

