        maximize(dp[(i+1)&1][nS], dp[i&1][S] + L);
        maximize(res, dp[(i+1)&1][nS]);
      }
    }
  }
  return res;
}

int main() {

  for(int N; ~scanf("%d", &N) && N;) {
    printf("%lld\n", solve(N));
  }

  return 0;
}

