ll solve(const vector<bitset<23>> &sch, vector<ll> &cost, int n, int k, bitset<23> now, ll c) {
  if(n == k) {
    return c;
  } else {
    if((sch[k] & now).any()) {
      return solve(sch, cost, n, k+1, now, c);
    } else {
      return max(solve(sch, cost, n, k+1, now, c), solve(sch, cost, n, k+1, now | sch[k], c + cost[k]));
    }
  }
}

