      for(int j=0;j<m;++j) {
        int l, r; cin >> l >> r;
        bs |= bitset<23>(((1ll<<r)-1) - ((1ll<<l)-1));
      }
      sch[i] = bs;
    }

    auto now = bitset<23>();
    cout << solve(sch, cost, n, 0, now, 0) << endl;
  }
}

