{
	if (pos < 0) return (0);
	if (memo[pos][bits] >= 0) return (memo[pos][bits]);

	lint &res = memo[pos][bits];

	res = getMax(pos - 1, bits);
	if ((bits & b[pos]) == 0)
		res = max(res, getMax(pos - 1, bits ^ b[pos]) + l[pos]);

	return (res);
}

