	// output("./outputs/0.txt");

	int N;
	while (cin >> N, N) {
		VI satisfactions(N);
		VULL guys(N, 0);
		REP (i, N) {
			int M; cin >> M >> satisfactions[i];
			REP (j, M) {
				int S, E; cin >> S >> E;
				S -= 6; E -= 6;
				guys[i] |= (1 << E) - (1 << S);
			}
		}

		VI dp(1 << 17, 0);
		REP (i, N) {
			for (ULL bit = 0; bit < 1 << 17; ++bit) {
				if ( (guys[i] & bit) == 0 ) {
					dp[guys[i] | bit] = max(dp[guys[i] | bit], dp[bit] + satisfactions[i]);
				}
			}
		}

		int ans = 0;
		REP (bit, 1 << 17) {
			ans = max(ans, dp[bit]);
		}
		cout << ans << endl;
	}

	return 0;
}

