ll solve(int n, int k, const vector<vector<pair<int, int>>> &guy, const vector<ll> &cost, const vector<ll> &v) {
  if(n == k) {
    ll res = 0;
    map<int, bool> m;
    for(int i=0;i<SZ(v);++i) {
      if(v[i] != -1 && !m[v[i]]) {
        res += cost[v[i]];
        m[v[i]] = true;
      }
    }
    // printv(v);
    return res;
  } else {
    int sz = guy[k].size();
    ll res = 0;
    for(int i=0;i<sz;++i) {
      vector<ll> newv(v);
      int s = guy[k][i].first;
      int e = guy[k][i].second;
      bool ok = true;
      for(int j=s;j<e;++j) {
        // cout << k << ":" << s << ":" << e << endl;
        // printv(v);
        if(newv[j] == -1) {
          newv[j] = k;
        } else {
          ok = false;
        }
      }
      if(ok) {
        res = max(res, solve(n, k+1, guy, cost, newv));
      }
    }
    res = max(res, solve(n, k+1, guy, cost, v));
    return res;
  }
}

