struct Guy {
  int B;
  intmax_t L;
  Guy(int B, intmax_t L): B(B), L(L) {}
};

int testcase_ends() {
  int N;
  scanf("%d", &N);

  if (N == 0)
    return 1;

  std::vector<Guy> guys   ;
  for (int i=0; i<N; ++i) {
    int M;
    intmax_t L;
    scanf("%d %jd", &M, &L);

    int B=0;
    for (int j=0; j<M; ++j) {
      int S, E;
      scanf("%d %d", &S, &E);
      S -= 6;
      E -= 6;
      B |= ((1<<E)-1) ^ ((1<<S)-1);
    }
    guys.emplace_back(B, L);
  }

  // tsurai
  std::vector<intmax_t> dp(1<<16);
  intmax_t res=0;
  for (size_t j=0; j<guys.size(); ++j) {
    for (size_t i=dp.size(); i--;) {
      int next=guys[j].B;
      if (i & next) continue;

      dp[i|next] = std::max(dp[i|next], dp[i]+guys[j].L);
      res = std::max(res, dp[i|next]);
    }
  }

  printf("%jd\n", res);
  return 0;
}

int main() {
  while (true)
    if (testcase_ends())
      return 0;
}

