        st-=6; ed-=6;
        int mask=0;
        for(int i=st; i<ed; i++) mask^=(1<<i);
        rep(bit, (1<<k)){
          chmax(nex[bit], dp[bit]);
          if((bit&mask)==mask and dp[bit^mask]>=0){
            chmax(nex[bit], dp[bit^mask]+x);
          }
        }
      }
      dp.swap(nex);
    }
    i64 mx=0;
    rep(bit, (1<<k)) chmax(mx, dp[bit]);
    cout<< mx<< endl;
  }
  return 0;
}

