int dfs(int t){
	if(memo.find(t)!=memo.end())return memo[t];
	int r=0;
	for(int i=0;i<n;i++)if((t&T[i])==T[i]){
		int x=S[i]+dfs(t&(~T[i]));
		if(r<x)r=x;
	}
	return memo[t]=r;
}

