  while(cin >> N,N)
    {
      //clock_t st,ed;
      //st = clock();
      vector<P> info;
      for(int i=0;i<N;i++)
	{
	  int M;
	  long long L;
	  cin >> M >> L;
	  int time = 0;
	  for(int j=0;j<M;j++)
	    {
	      int S,E;
	      cin >> S >> E;
	      int pre = 0;
	      pre = (1<<E)-1;
	      pre ^= (1<<S)-1;
	      time |= pre;
	    }
	  info.push_back(P(L,time));
	}
      long long ans = 0;
      deque<Pox> deq;//Pox(P(満足度、使った時間),会った人)
      for(int i=0;i<N;i++)
	deq.push_back(Pox(info[i],(1<<i))),ans = max(ans,info[i].first);
