#include<iostream>
#include<algorithm>
using namespace std;

#define MAX_Q 100000
#define MAX_N 120
#define MAX_M 18
#define MAX_T 17

long long dp[1 << MAX_M];
long long dp2[1 << MAX_M];

long long P[MAX_N][MAX_N][2];
long long N, M[MAX_M], D[MAX_N], L, R, cnt;

long long power(int b) {
	int res = 1;
	for (int i = 0; i < b; i++) {
		res *= 2;
	}
	return res;
}

long long bit_dp() {
	long long cnt = 0;
	for (int i = 0; i < N; i++)
	{
		for (int j = 0; j < (1 << MAX_T); j++)
		{
			long long V[MAX_M], W[MAX_M];
			for (int k = 0; k < MAX_T; k++) {
				V[k] = (j / power(k)) % 2;
				W[k] = V[k];
			}

			for (int k = 0; k < M[i]; k++)
			{
				L = P[i][k][0], R = P[i][k][1];
				for (int l = L; l < R; l++)
				{
					if (V[l] == 1)
					{
						goto E;
					}
				}
				for (int l = 0; l < MAX_T; l++) {
					W[l] = V[l];
				}
				for (int l = L; l < R; l++) {
					W[l] = 1;
				}
				cnt = 0;
				for (int l = 0; l < MAX_T; l++) {
					cnt += W[l] * (1LL << l);
				}
				if (cnt < (1 << MAX_T)) {
					dp2[cnt] = max(dp2[cnt], dp[j] + D[i]);
				}
			E:;
			}
		}
		for (int j = 0; j < (1 << MAX_T); j++)
		{
			dp[j] = max(dp[j], dp2[j]);
			dp2[j] = 0;
		}
	}
	long long maxn = 0;
	for (int i = 0; i < (1 << MAX_T); i++)
	{
		maxn = max(maxn, dp[i]);
	}
	return maxn;
}

void _memset() {
	for (int i = 0; i < (1 << MAX_M); i++) {
		dp[i] = 0;
		dp2[i] = 0;
	}
	for (int i = 0; i < MAX_N; i++) {
		D[i] = 0;
		M[i] = 0;
		for (int j = 0; j < MAX_N; j++) {
			P[i][j][0] = 0;
			P[i][j][1] = 0;
		}
	}
}

int main() {
	int cnt3 = 0;
	while (true)
	{
		_memset();
		cin >> N;
		if (N == 0) {
			break;
		}
		for (int i = 0; i < N; i++)
		{
			cin >> M[i] >> D[i];
			for (int j = 0; j < M[i]; j++) {
				cin >> P[i][j][0] >> P[i][j][1];
				P[i][j][0] -= 6;
				P[i][j][1] -= 6;
			}
		}
		cout << bit_dp() << endl;
		cnt3++;
	}
	return 0;
}

