		bit = 0;
	}
};

int set( int p, vector< int > s, vector< int > e ) {
	for( int i = 0; i < s.size(); i++) {
		for( int j = 0; j < e[i] - s[i] ; j++) {
			p += ( 1 << ( j + s[i] ) );
		}
	}
	return p;
}

unsigned long long int max( unsigned long long int p, unsigned long long int q ) {
	if( p > q ) return p;
	return q;
}

int main(void){


	vector< vector< unsigned long long int > > dp( 100, vector< unsigned long long int> ( (1 << 16 ), 0 ) );

	while( true ) {
		int N;
		vector< guy > guys;

		/*
			dp[人の数][スケジュールのbit] = 効用の最大値
		*/

//		unsigned long long int dp[100][1 << 16] = {0};
		for( int i = 0; i < 100; i++) {
			for( int j = 0; j < ( 1 << 16 ) ; j++ ) {
				dp[i][j] = 0;
			}
		}

		unsigned long long int ans = 0;
		cin >> N;
		if( N == 0 ) break;

		for( int i = 0; i < N; i++ ) {
			guy g;
			cin >> g.M >> g.L;
			for( int j = 0; j < g.M; j++) {
				int tmp, tmp2;
				cin >> tmp >> tmp2;
				tmp -= 6;
				tmp2 -= 6;
				g.S.push_back( tmp );
				g.E.push_back( tmp2 );
			}
			g.bit = set( 0, g.S, g.E );
			guys.push_back( g );
		}

		for( int i = 0; i < N; i++ ) {
			dp[i][set(0, guys[i].S, guys[i].E )] = guys[i].L;
		}
		for( int i = 1; i < 16; i++) {
			for( int j = 0; j < 1 << 16; j++ ) {
				for( int k = 0; k < N; k++ ) {
					if( !( j & guys[k].bit ) ) {
						int tmp = ( j | guys[k].bit );
						dp[i][ tmp ] = max( dp[i][tmp], dp[i-1][j] + guys[k].L );
					}
				}
			}
		}

		for( int i = 0; i < 1 << 16; i++) {
			for( int j = 0; j < N; j++ )
				ans = max( dp[j][i], ans );
		}

		cout << ans << endl;
	}

	return 0;
}

#include<iostream>
#include<algorithm>
using namespace std;

#define MAX_N 120
#define MAX_M 16
int dp[1 << (MAX_M + 1)];
int dp2[1 << (MAX_M + 1)];
int P[MAX_N][MAX_N][2];
int N, M[MAX_M], Q[MAX_N];

int power(int le, int ri) {
	int cnt2 = 0;
	for (int i = le; i < ri; i++) {
		cnt2 += (1 << i);
	}
	return cnt2;
}

int bit_dp() {
	int cnt = 0;
	for (int i = 1; i <= N; i++) {
		for (int j = 0; j < (1 << MAX_M); j++) {
			int V[MAX_M + 1];
			for (int k = 0; k < MAX_M; k++) {
				V[k] = (j / (1 << k)) % 2;
			}
			for (int k = 0; k < M[i]; k++) {
				int L = P[i][k][0], R = P[i][k][1];
				for (int l = L; l < R; l++) {
					if (V[l] == 1) {
						goto E;
					}
				}
				cnt = j + power(L, R);
				dp2[cnt] = max(dp2[cnt], dp[j] + Q[i]);
			E:;
			}
		}
		for (int j = 0; j < (1 << (MAX_M + 1)); j++) {
			dp[j] = max(dp[j], dp2[j]);
			dp2[j] = 0;
		}
	}
	int maxn = 0;
	for (int i = 0; i < (1 << (MAX_M + 1)); i++) {
		maxn = max(maxn, dp[i]);
	}
	return maxn;
}

void _memset() {
	for (int i = 0; i < (1 << MAX_M + 1); i++) {
		dp[i] = 0;
		dp2[i] = 0;
	}
	for (int i = 0; i < MAX_N; i++) {
		Q[i] = 0;
		for (int j = 0; j < MAX_N; j++) {
			P[i][j][0] = 0;
			P[i][j][1] = 0;
		}
	}
}

int main() {
	while (true) {
		cin >> N;
		if (N == 0) {
			break;
		}
		for (int i = 1; i <= N; i++) {
			cin >> M[i] >> Q[i];
			for (int j = 0; j < M[i]; j++) {
				cin >> P[i][j][0] >> P[i][j][1];
				P[i][j][0] -= 6;
				P[i][j][1] -= 6;
			}
		}
		cout << bit_dp() << endl;
	}
	return 0;
}

