typedef long long ll;
const int BIT = 22-6+1;
const int N = 100;
ll dp[N][1<<BIT];

ll solve(int n,int *mask,ll *val){
  for(int i=0;i<n;i++)
    for(int j=0;j<(1<<BIT);j++)dp[i][j]=0;
  dp[0][0]=0;
  dp[0][mask[0]]=val[0];
  for(int i=1;i<n;i++){
    for(int j=0;j<(1<<BIT);j++){
      dp[i][j]=max(dp[i][j],dp[i-1][j]);
      if (j&mask[i])continue;
      dp[i][j|mask[i]] = max(dp[i][j|mask[i]],dp[i-1][j]+val[i]);
    }
  }
  ll ret = 0;
  for(int i=0;i<(1<<BIT);i++)ret=max(ret,dp[n-1][i]);
  return ret;
}

