ll N;

ll rec(ll itr, ll bit) {
    if (itr == N) return 0;

    if (dp[itr][bit] >= 0) return dp[itr][bit];

    dp[itr][bit] = rec(itr + 1, bit);
    if ((bit & guy[itr].snd) == 0) {
        dp[itr][bit] = max(dp[itr][bit], rec(itr + 1, bit + guy[itr].snd) + guy[itr].fst);
    }

    return dp[itr][bit];
}

