using namespace std;

#define MAX_Q 100000
#define MAX_N 120
#define MAX_M 17
#define MAX_T 16

long long dp[1 << MAX_M];
long long dp2[1 << MAX_M];

long long P[MAX_N][MAX_N][2];
long long N, M[MAX_N], D[MAX_N], L, R, cnt;
long long power[MAX_T];
int hantei[1 << MAX_T][MAX_T][MAX_T];

long long power2(int L2, int R2) {
	int res4 = 0;
	for (int i = L2; i <= R2; i++) {
		res4 += power[i];
	}
	return res4;
}

void HANTEI() {
	for (int i = 0; i < (1 << MAX_T); i++)
	{
		for (int j = 0; j < MAX_T; j++)
		{
			for (int k = j; k < MAX_T; k++)
			{
				int L1 = j, R1 = k;
				for (int l = L1; l <= R1; l++)
				{
					long long target = (i / power[l]) % 2;
					if (target == 1)
					{
						goto G;
					}
				}
				hantei[i][j][k] = i + power2(j, k);
				goto H;
			G:;
				hantei[i][j][k] = -1;
			H:;
			}
		}
	}
}

long long bit_dp() {
	long long cnt = 0;
	for (int i = 0; i < N; i++)
	{
		for (int j = 0; j < (1 << MAX_T); j++)
		{
			for (int k = 0; k < M[i]; k++)
			{
				if (hantei[j][P[i][k][0]][P[i][k][1]] >= 0) {
					int t1 = hantei[j][P[i][k][0]][P[i][k][1]];
					dp2[t1] = max(dp2[t1], dp[j] + D[i]);
				}
			}
		}
		for (int j = 0; j < (1 << MAX_T); j++)
		{
			dp[j] = max(dp[j], dp2[j]);
			dp2[j] = 0;
		}
	}
	long long maxn = 0;
	for (int i = 0; i < (1 << MAX_T); i++)
	{
		maxn = max(maxn, dp[i]);
	}
	return maxn;
}

void _memset() {
	for (int i = 0; i < MAX_M; i++) {
		if (i == 0) {
			power[i] = 1;
		}
		else {
			power[i] = power[i - 1] * 2;
		}
	}
	for (int i = 0; i < (1 << MAX_M); i++) {
		dp[i] = 0;
		dp2[i] = 0;
	}
	for (int i = 0; i < MAX_N; i++) {
		D[i] = 0;
		M[i] = 0;
		for (int j = 0; j < MAX_N; j++) {
			P[i][j][0] = 0;
			P[i][j][1] = 0;
		}
	}
}

int main() {
	int cnt3 = 0;
	_memset();
	HANTEI();
	while (true)
	{
		_memset();
		cin >> N;
		if (N == 0) {
			break;
		}
		for (int i = 0; i < N; i++)
		{
			cin >> M[i] >> D[i];
			for (int j = 0; j < M[i]; j++) {
				cin >> P[i][j][0] >> P[i][j][1];
				P[i][j][0] -= 6;
				P[i][j][1] -= 6;
			}
		}
		cout << bit_dp() << endl;
		cnt3++;
	}
	return 0;
}

