template <class T, class U>
inline bool chmax(T& t, const U& f) { return t < f ? t = f, true : false; }

int solve(int N, const vector<int>& L, const vector<int>& D) {
    vector<int> mem(1 << 16, -1);
    function<int(int)> dfs = [&](int S) {
        int& t = mem[S];
        if (t != -1) return t;
        t = 0;
        rep(i, N) if ((S & D[i]) == D[i]) chmax(t, L[i] + dfs(S - D[i]));
        return t;
    };
    return dfs((1 << 16) - 1);
}

int main() {
    for (int N; cin >> N, N;) {
        vector<int> L(N), D(N);
        rep(i, N) {
            int M; cin >> M >> L[i];
            while (M--) {
                int S, E; cin >> S >> E;
                D[i] |= -1 & (1 << (E - 6)) - 1 ^ (1 << (S - 6)) - 1;
            }
        }
        cout << solve(N, L, D) << endl;
    }
}

