using namespace std;

int res, n, u[101], f[101], L[101];
vector<int> g[101], T[24];

void dfs(int x)
{
    u[x]++;
    for(int v: g[x]) f[v]++;

    int c=0;
    for(int i=x+1;i<n;++i) if (!f[i] and !u[i]) {
        dfs(i);
        ++c;
    }
    if (c==0) {
        int r=0;
        for(int i=0;i<n;++i) if (u[i]) r+=L[i];
        res=max(res, r);
    }

    for(int v: g[x]) f[v]--;
    u[x]--;
}

int main(void)
{
    while (scanf("%d", &n), n) {
        for(int i=0;i<n;++i) g[i].clear();
        for(int i=0;i<24;++i) T[i].clear();
        for(int i=0;i<n;++i) {
            int m;
            scanf("%d%d", &m, L+i);
            for(int j=0;j<m;++j) {
                int s, e;
                scanf("%d%d", &s, &e);
                for(int k=s;k<e;++k) T[k].push_back(i);
            }
            for(int j=0;j<24;++j)
                for(int k: T[j]) for(int l: T[j]) g[k].push_back(l);
            for(int j=0;j<n;++j) {
                sort(begin(g[j]), end(g[j]));
                g[j].erase(unique(begin(g[j]), end(g[j])), end(g[j]));
            }
        }
        res=0;
        memset(u, 0, sizeof(u));
        memset(f, 0, sizeof(f));
        for(int i=0;i<n;++i) dfs(i);
        printf("%d\n", res);
    }
    return 0;
}

