using P = pair<int, int>;

int N;
vector<vector<P> > G;
vector<int> M, L;
vector<vector<int> > dp;

int dfs(int n, int bit) {
  if ( n == N ) return 0;
  if ( dp[n][bit] >= 0 ) return dp[n][bit];

  int ret = dfs(n+1, bit);
  bool flag = true;
  int nxt = bit;  
  for ( int i = 0; i < M[n]; i++ ) {
    int s = G[n][i].first, e = G[n][i].second;
    for ( int j = s; j < e; j++ ) {
      if ( nxt & (1<<j) ) {
	flag = false;
	break;
      }
      nxt |= (1<<j);      
    }
  }

  if ( flag ) ret = max(ret, dfs(n+1, nxt)+L[n]);
  return dp[n][bit] = ret;  
}

