    M = L = vector<int>(N);    
    for ( int i = 0; i < N; i++ ) {
      cin >> M[i] >> L[i];
      for ( int j = 0; j < M[i]; j++ ) {
	int s, e;
	cin >> s >> e;
	s -= 6;
	e -= 6;
	G[i].emplace_back(P(s, e));	
      }
    }

    dp = vector<vector<int> >(N, vector<int>(1<<17, -1));
    cout << dfs(0, 0) << endl;
  }

  return 0;
}

