      }
      guy[i].mask = mask;
      guy[i].satisfaction_vol = satisfaction_vol;
    }

    for(int i=0;i<guy_num;i++){
      for(int S=0;S<=(1<<17)-1;S++){
    	if((S & guy[i].mask) == 0){
