    	  dp[i+1][next] = max(max(dp[i][next],dp[i+1][next])
    			   ,dp[i][S]+guy[i].satisfaction_vol);
    	}
    	else{
    	  dp[i+1][S] = max(dp[i+1][S],dp[i][S]);
    	}
      }
    }

    int max_v = 0;
    for(int S=0;S<=(1<<17)-1;S++) max_v = max(dp[guy_num][S],max_v);
    printf("%d\n",max_v);
  }
}

